87.source:
function extendColor() {
    Color.addMethods({
        luminance: function () {
             return 0.2126 * this.r + 0.7152 * this.g + 0.0722 * this.b;
        },
        hue: function () {
            return (this.r + (this.b * 2 + 1) + (this.g * 3 + 2)) / 9.0
        },
        isValid: function () {
            return this.r >= 0 && this.g >= 0 && this.b >= 0 &&
                    this.r <= 1 && this.g <= 1 && this.b <= 1
        }
    });
}

90.source:
function constrainColor(color, prevColor, solver) {
    bbb.always({
        solver: solver,
        ctx: {
            solver: solver,
            color: color,
            prevColor: prevColor,
            _$_self: this.doitContext || this
        }
    }, function() {
        return color.getFill().isValid() && color.luminance == color.getFill().luminance() && color.hue == color.getFill().hue() && _$_self.luminance * .8 <= color.luminance && _$_self.hue * .8 <= color.hue;;
    });
    if (prevColor) {
        bbb.always({
            solver: solver,
            ctx: {
                solver: solver,
                color: color,
                prevColor: prevColor,
                _$_self: this.doitContext || this
            }
        }, function() {
            return color.lumSum == color.luminance + prevColor.lumSum && color.hueSum == color.hue + prevColor.hueSum;;
        });
    } else {
        bbb.always({
            solver: solver,
            ctx: {
                solver: solver,
                color: color,
                prevColor: prevColor,
                _$_self: this.doitContext || this
            }
        }, function() {
            return color.lumSum == color.luminance && color.hueSum == color.hue;;
        });
    }
}

