function constrainColor(color, prevColor, solver) {
    always: {solver: solver;
        color.getFill().isValid() &&
        color.luminance == color.getFill().luminance() &&
        color.hue == color.getFill().hue() &&
        this.luminance * 0.8 <= color.luminance &&
        this.hue * 0.8 <= color.hue
    };

    if (prevColor) {
        always: {solver: solver;
            color.lumSum == color.luminance + prevColor.lumSum &&
            color.hueSum == color.hue + prevColor.hueSum
        };
    } else {
        always: {solver: solver;
            color.lumSum == color.luminance &&
            color.hueSum == color.hue;
        };
    }
}

