104.source:
function setupMorphs() {
    this.extendColor();
    bbb.unconstrain(this, "hue");
    bbb.unconstrain(this, "luminance")

    // fill with "pixels"
    this.removeAllMorphs();
    this.setExtent(lively.pt(300, 300));

    var pixelCount = parseInt(this.get("NumColors").textString),
        sidel = Math.floor(300 / pixelCount),
        self = this,
        pixelCount = this.getExtent().x / sidel,
        lastPx;
    this.luminance = 0; this.hue = 0.2; this.colors = [];

    for (var x = 0; x < this.getExtent().x; x += sidel) {
        var px = lively.morphic.Morph.makeRectangle(0, 0, sidel, 300);
        px.luminance = 0; px.lumSum = 0; px.hue = 0; px.hueSum = 0;

        px.onMouseUp = function (evt) {
            self.openColorChooser(evt, this);
        }.bind(px);
        px.setBorderWidth(1);
        px.setFill(Color.rgb(255,255,255));
        this.addMorph(px);
        px.setPosition(lively.pt(x, 0));
        lastPx = px;
        this.colors.push(px);
    }
}

107.source:
function createConstraints() {
    var prevColor,
        solver = this.getSolver();

    this.colors.each(function (color) {
        this.constrainColor(color, prevColor, solver);
        prevColor = color;
    }.bind(this));
    this.constrainColorProperties(prevColor, this.colors.length, solver);
}

