function reset() {
    this.stopStepping();

    ["x", "d", "y", "s", "xString", "dString"].each(function (prop) {
        bbb.unconstrain(this, prop);
    }.bind(this));
    this.x = this.y = 0;
    this.s = "";
    this.d = new Date();

    this.region1_deltablue = new DBPlanner();
    this.region1_deltablue.weight = 1000;
    this.region2_cassowary = new ClSimplexSolver();
    this.region2_cassowary.weight = 500;
    this.region3_deltablue = new DBPlanner();
    this.region3_deltablue.weight = 250;

    this.startStepping(100, "updateLabel");
}

