function example() {
    this.reset();
    this.xString = this.get("xString").textString;
    this.dString = new Date(this.get("dString").textString);
    bbb.always({
        solver: this.region1_deltablue,
        ctx: {
            Date: Date,
            parseInt: parseInt,
            _$_self: this.doitContext || this
        }
    }, function() {
        return _$_self.x.formula([ _$_self.xString ], function() {
            return parseInt(_$_self.xString);
        });;
    });
    bbb.always({
        solver: this.region1_deltablue,
        ctx: {
            Date: Date,
            parseInt: parseInt,
            _$_self: this.doitContext || this
        }
    }, function() {
        return _$_self.d.formula([ _$_self.dString ], function() {
            return _$_self.dString;
        });;
    });
    bbb.always({
        solver: this.region2_cassowary,
        ctx: {
            Date: Date,
            parseInt: parseInt,
            _$_self: this.doitContext || this
        }
    }, function() {
        return 3 * _$_self.x + 2 * _$_self.y >= 10;;
    });
    bbb.always({
        solver: this.region2_cassowary,
        priority: "weak",
        ctx: {
            Date: Date,
            parseInt: parseInt,
            _$_self: this.doitContext || this
        }
    }, function() {
        return _$_self.y == 0;;
    });
    bbb.always({
        solver: this.region3_deltablue,
        ctx: {
            Date: Date,
            parseInt: parseInt,
            _$_self: this.doitContext || this
        }
    }, function() {
        return _$_self.s.formula([ _$_self.d, _$_self.y ], function() {
            return _$_self.d + ":" + _$_self.y;
        });;
    });
}

