function example() {
    this.reset();

    this.xString = this.get("xString").textString;
    this.dString = new Date(this.get("dString").textString);

    always: { solver: this.region1_deltablue
        this.x.formula([this.xString], function () {
            return parseInt(this.xString);
        });
    }
    always: { solver: this.region1_deltablue
        this.d.formula([this.dString], function () {
            return this.dString;
        })
    }

    always: { solver: this.region2_cassowary
        3*this.x + 2*this.y >= 10;
    }
    always: { solver: this.region2_cassowary; priority: "weak";
        this.y == 0;
    }

    always: { solver: this.region3_deltablue
        this.s.formula([this.d, this.y], function () {
            return this.d + ":" + this.y;
        })
    }
}

